import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Card from 'react-bootstrap/Card';
import service from '../../services/user'
import './Users.css'

// eslint-disable-next-line import/no-anonymous-default-export
export default () => {
  const [users, setUsers] = useState([]);
  const [since, setSince] = useState(0);

  useEffect(() => {
    fetchData();
  }, []);

  async function fetchData() {
    setSince(since + 10);
    try {
      const { data } = await service.listUsers(since);
      const formatedValue = data.map((user) => (
        <Link className="link" to={`/details/${user.login}`}><li>{user.id} - {user.login}</li></Link>
      ))
      formatedValue.push(users);
      setUsers(formatedValue)
    } catch (error) {
      console.error(error);
    }
  }

  return (
    <>
      <ul>
      <h2>USERS</h2>
        {users}
      </ul>
      <div className="flex-center">
        <button className="button-users" onClick={fetchData}>Show more</button>
      </div>
    </>
  )
}
