import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import service from '../../services/auth'

// eslint-disable-next-line import/no-anonymous-default-export
export default () => {
    const { login } = useParams();

    const [users, setUsers] = useState([]);

    useEffect(() => {
      fetchData();
    }, []);

    async function fetchData() {
      setSince(since + 10);
      try {
        const { data } = await service.listUsers(since);
        const formatedValue = data.map((user) => (
          <Link className="link" to={`/details/${user.login}`}><li>{user.id} - {user.login}</li></Link>
        ))
        formatedValue.push(users);
        setUsers(formatedValue)
      } catch (error) {
        console.error(error);
      }
    }

    return (
        <div>
            <h2>Details of {login}</h2>
        </div>
    )
}
